INSERT INTO `novel` (`id`, `user_id`, `category_id`, `title`, `status`, `author`, `img_link`, `total_chapters`, `pre_view`, `view_count`, `verify`, `create_at`) VALUES
(2, 1, 4, 'ĐẤU LA ĐẠI LỤC', 'Chưa hoàn thành', 'Đường Gia Tam Thiếu', '1640141735.jpg', 4, 'Một đại lục không hề yên bình, một cuộc sống đầy hiểm nguy, phiêu lưu nhưng cũng không kém phần lãng mạn. tình yêu, thù hận, trách nhiệm… Tiếp bước những tiền bối đi trước, Hoắc Vũ Hạo và đời sau sử lai khắc thất quái, bằng niềm tin nhiệt huyết tuổi trẻ đã gây dựng lại đường môn tái lập những huy hoàng xưa kia của các tiền bối đi trước….', 0, b'1', '2021-12-22 09:55:35'),
(3, 1, 3, 'MÊ VỢ KHÔNG LỐI VỀ', 'Chưa hoàn thành', 'Chiêu Tài Tiến Bảo', '1640143150.jpg', 5, 'Một cuộc giao dịch, cô mang thai con của người lạ, mang bụng bầu gả cho người đàn ông đã đính ước từ nhỏ. Vốn cho rằng chỉ là một cuộc giao dịch, lại dây dưa thứ tình cảm không nên có trong cuộc hôn nhân này. Mười tháng hoài thai sắp sinh, một tờ đơn ly hôn trên đất, cô mới hoàn toàn tình ngộ. Sau này anh ta nói \"Bà xã về đi, người anh yêu luôn là em\"', 0, b'1', '2021-12-22 10:19:10'),
(8, 1, 11, 'Hacker mũ trắng', 'Chưa hoàn thành', 'Nong béo weeboo', '1640487367.jpg', 0, 'Tôi đã trở thành hackerprochuyennghiepvíp@chuan như thế nào', 0, b'1', '2021-12-23 10:01:40'),
(15, 1, 4, 'ĐẤU PHÁ THƯƠNG KHUNG', 'Chưa hoàn thành', 'Thiên Tàm Thổ Đậu', '1640607310.jpg', 3, 'Đấu Phá Thương Khung là một câu chuyện huyền huyễn đặc sắc kể về Tiêu Viêm, một thiên chi kiêu tử với thiên phú tu luyện mà ai ai cũng hâm mộ, bỗng một ngày người mẹ mất đi đễ lại di vật là một chiếc giới chỉ màu đen nhưng từ khi đó Tiêu Viêm đã mất đi thiên phú tu luyện của mình.', 0, b'0', '2021-12-24 11:04:44'),
(16, 1, 4, 'THẾ GIỚI HOÀN MỸ', 'Chưa hoàn thành', 'Thần Đông', '1640607368.jpg', 3, 'Một hạt bụi có thể lấp biển, một cọng cỏ chém hết mặt trời mặt trăng và ngôi sao, trong nháy mắt ở giữa long trời lỡ đất. Quần hùng cùng nổi lên, vạn tộc mọc lên san sát như rừng, chư thánh tranh bá, loạn khắp đất trời. Hỏi mặt đất bao la, cuộc đời thăng trầm? Một thiếu niên theo trong đất hoang đi ra, tất cả bắt đầu từ nơi này...', 0, b'0', '2021-12-24 11:05:59'),
(17, 1, 3, 'RỂ QUÝ TRỜI CHO', 'Chưa hoàn thành', 'Quỷ Thượng Nhân', '1640607410.jpg', 3, 'Truyện Rể Quý Trời Cho của tác giả Quỷ Thượng Nhân với nội dung truyện xoay quanh nhân vật chính – Lâm Thanh Diện: Khi đó mẹ Lâm Thanh Diện vì muốn nắm quyền nhà họ Lâm mà không tiếc để anh gánh tội danh phản bội, anh phải rời khỏi gia tộc, làm con rể phế vật bị người người chê cười ở Giang Thành. Cho đến khi vợ bị ức hiếp, anh mới không thể tiếp tục điệu thấp nữa, lộ thân phận, vả mặt bọn người.', 0, b'0', '2021-12-24 21:09:39'),
(18, 1, 1, 'LINH VŨ THIÊN HẠ', 'Chưa hoàn thành', 'Vũ Phong', '1640607456.jpg', 3, 'Lục Thiếu Du, linh hồn bị xuyên qua đến thế giới khác, nhập vào thân thể của một thiếu gia không có địa vị phải trải qua cuộc sống không khác gì nô bộc.\r\nThế giới này lấy Vũ vi cường, lấy Linh vi tôn, nghe đồn khi võ đạo đỉnh phong, linh đạo đạt đến cực hạn có thể phá toái hư không.\r\nLục Thiếu Du mang theo ký ức từ kiếp trước tái sinh, không cam lòng làm một thiếu gia chẳng khác gì củi mục.\r\n\r\nTrong thế giới xa lạ,\r\nSay - nằm trên gối mỹ nhân,\r\nTỉnh - nắm quyền thiên hạ.\r\nĐây mới là cuộc sống đáng mơ ước.\r\nLinh - Vũ song tu\r\nBá chủ kiêu hùng\r\nĐã đến, ta sẽ lưu lại một huyền thoại......', 0, b'0', '2021-12-24 21:10:16'),
(19, 1, 3, 'VỢ HỜ YÊU CỦA TỔNG TÀI', 'Chưa hoàn thành', 'Tha Y Y', '1640607551.jpg', 3, 'Văn Án:\r\n\r\nBạn đang đọc truyện Vợ Hờ Yêu Của Tổng Tài của tác giả Tha Y Y. Người đàn ông sống dở chết dở kia ngã đè lên sau lưng Tần Hoài An làm cô suýt nữa thì tắt thở.\r\n\r\nTần Hoài An giơ tay đẩy anh ta ra, giãy giụa muốn bò cơ mà anh ta nặng chết đi được!\r\n\r\nNgười đàn ông vẫn không nhúc nhích!\r\n\r\nTần Hoài An nhíu mày, cô dùng sức giãy giụa hơn.\r\n\r\nBên eo cô đột nhiên siết chặt.', 0, b'0', '2021-12-27 19:19:11'),
(20, 1, 3, 'CÔ DÂU BỊ ĐÁNH TRÁO CỦA TỔNG TÀI', 'Chưa hoàn thành', 'Phủng Anh', '1640607639.jpg', 3, 'An Nhiên thấy Tống Thành đột ngột xông đến thì vội giơ tay ra đỡ, sợ hắn bị choáng váng sau cú tông Cô lay gọi: “Đừng ngất! Tôi đưa anh đi bệnh viện”\r\n\r\nKhuôn mặt đẹp đế của Tống Thành áp vào hõm cổ An Nhiên, thở ra một hơi nóng rẩy như sốt cao.\r\n\r\nĐúng lúc đó, một chiếc lao đến, bật đèn sáng choang. Một tay An Nhiên đỡ thân thể nặng nề của Tống Thành, một tay vẫy loạn xạ ra tín hiệu gọi xe kia dừng lại. Chiếc xe màu đỏ rực đi chậm lại, đến gần hai người. An Nhiên đột nhiên thấy cảm giác quen thuộc, chưa kịp gọi thì đột nhiên tài xế kia rồ ga vọt đi mất. Trước khi đi qua, bánh xe còn hắt nguyên một vũng nước mưa tạt thẳng từ đầu tới chân cô ướt sũng.\r\n\r\nBên cạnh đó, bạn đừng bỏ lỡ những truyện ngôn tình hay như: Em Thực Sự Không Muốn hay Mỗi Lần Đều Chết Trong Lòng Nam Chính', 0, b'0', '2021-12-27 19:20:39'),
(21, 1, 3, 'CƯNG CHIỀU VỢ NHỎ TRỜI BAN', 'Chưa hoàn thành', 'Lưu Ly Tuyết Tuyết', '1640607759.jpg', 3, 'Hạ Tịch Quán được gả vào U Lan Uyển đó là một âm mưu. Hạ Tịch Quán là một mỹ nhân nghiêng nước nghiêng thành còn chồng cô thì ngược lại một người đàng ông với tướng mạo xấu xí, bác sĩ phế tài.\r\n\r\n**********\r\n\r\nBây giờ, Hạ Tịch Quán thật lòng cảm ơn mẹ kế Lý Ngọc Lan của cô, Lý Ngọc Lan đón cô đến Hải Thành, chỉ cho cô ngồi tàu hỏa giá rẻ, nhưng bà ta lại tổ chức buổi hôn lễ cực kỳ xa hoa hoành tráng, muốn giành được danh tiếng tốt cho bà ta.\r\n\r\nCon gái Hạ gia gả vào U Lan Uyển xung hỉ, đây chính là tin được buôn dưa lớn nhất Hải Thành, Hạ Tịch Quán đang đánh cược, đánh cược người đàn ông này không muốn chọn phiền toái.', 0, b'0', '2021-12-27 19:22:39'),
(22, 1, 3, 'LÀM NŨNG - THỜI TINH THẢO', 'Chưa hoàn thành', 'Thời Tinh Thảo', '1640607927.jpg', 4, 'Tại một bữa tiệc chúc mừng nào đó, Giang Hoài Khiêm khoan thai đến muộn.\r\n\r\nVừa xuất hiện, người vừa nhận giải là Nguyễn Khinh Họa, đang say khướt cầm bình rượu đi về phía anh.\r\n\r\nĐồng nghiệp của cô thấy vậy đều hoảng sợ, muốn đem người giữ lại, lại bị cô gạt ra\r\n', 0, b'0', '2021-12-27 19:25:27');

